/***
 * get activity name from url if exists and return the project string if in samples
 * @returns {string}
 */
IDE_Morph.prototype.getActivityFromUrl = function () {
    var values = {};
    var sampleName;
    var initproject=initsrc;
    var items = document.URL.match(new RegExp("[\\?&][^=&#]*=[^&#]*", "g"));
    if (items != null)
        for (var i = 0; i < items.length; i++) {
            var l = items[i].indexOf("=");
            var name = items[i].substring(1, l);
            var value = items[i].substring(l + 1);
            values[name] = decodeURIComponent(value);
        }
    sampleName=values['activity'];
    if(samples[sampleName] != null){
                initproject=samples[sampleName];
        }
    console.log('>>>>>>>>>>>>>>>>>>'+samples[sampleName]);
    return initproject;
}

